INSERT INTO `OPRISK_BOOK_STORE` (`OPRISK_BOOK_STORE_ID`, `TITLE`, `AUTHOR`, `PUBLISH_DATE`, `SUB_TITLE`, `ORIGIN_TITLE`, `IMAGE`, `TRANSLATOR`, `PAGES`, `PRICE`, `PUBLISHER`, `ISBN_10`, `ISBN_13`, `AUTHOR_INTRO`, `SUMMARY`, `BOOK_OWNER`, `IS_IN_LIBRARY`, `IS_ACTIVE`, `DESCRIPTION`, `CREATED_BY`, `CREATED_DATE`) VALUES (13,'英语语法新思维初级教程','[\"张满胜\"]','2008-1',NULL,'','http://img4.douban.com/mpic/s5921507.jpg','[]',298,'38.00元','群言出版社','7800808351','9787800808357','张满胜：英国剑桥大学英语硕士，北京新东方学校“新思维语法”培训创始人。拥有十多年的英语教学和研究经验，对英语语法有深入研究，目前正致力于考研英语的培训和研究工作。2003年以来一直在《新东方英语》杂志主持“新思维语法”专栏，深受广大读者的欢迎。','《英语语法新思维初级教程:走近语法》是一本“与众不同”的语法书：它不同于传统语法书只是机械地罗列教条式的“死规则”，它真的要告诉你规则背后的“活思维”；它不同于传统语法书追求“大而全”的规则排列，它真的要让你快速掌握“少而精”的思维规律；它不同于传统语法书那样本末倒置地给出较多生僻的“例外”，它真的让你高效掌握英语思维的本质；它不同于传统语法书采用枯燥的例句来讲解规则，读起来味同嚼蜡，它真的要让你在欣赏有趣例句、幽默故事以及精美短文的同时，领悟英语思维；它不同于传统语法书只给你孤立的、脱离语境的单一句子，它真的要让你知道例句背后的使用语境；它不同于传统语法书只教你语法知识，它真的要教你如何运用语法知识，从而把语法知识转化为你的语言技能，真正培养你创造性使用英语的能力。','oprisk','Y',NULL,NULL,'admin',NULL);
